#include<iostream>#include<bits/stdc++.h>using namespace std;void setZeroes1(vector<vector<int>>& matrix) {        int col0 = 1, rows = matrix.size(), cols = matrix[0].size();        for (int i = 0; i < rows; i++) {        if (matrix[i][0] == 0) col0 = 0;        for (int j = 1; j < cols; j++)            if (matrix[i][j] == 0)                matrix[i][0] = matrix[0][j] = 0;    }    for (int i = rows - 1; i >= 0; i--) {        for (int j = cols - 1; j >= 1; j--){            if (matrix[i][0] == 0 || matrix[0][j] == 0)                matrix[i][j] = 0;        }        if (col0 == 0) matrix[i][0] = 0;    }    }void setZeroes2(vector<vector<int>>& matrix) {        int col0 = 1, rows = matrix.size(), cols = matrix[0].size();                vector<pair<int,int>> v;        for(int i=0;i<rows;i++){            for(int j=0;j<cols;j++){                if(matrix[i][j] == 0){                    v.push_back({i,j});                }            }        }        for(auto it : v){            int i = it.first;            int j = it.second;                        for(int k=0;k<cols;k++){                matrix[i][k] = 0;            }            for(int k=0;k<rows;k++){                matrix[k][j] = 0;            }        }    }int main(){	vector<vector<int>> matrix = { {1,1,0},						 {1,1,1},						 {0,1,1} };	setZeroes1(matrix);	for(int i=0;i<3;i++){		for(int j=0;j<3;j++){			cout<<matrix[i][j]<<" ";		}		cout<<endl;	}}